Scripts.OnClick:
if (IsAddOnLoaded("GoH_Druid")) then

		GoH_Druid_Form:Show();
		GoH_DeathKnight_Form:Hide();
		else
		GameTooltip:SetText(("Plugin not installed"), 1.0, 0.8, 0.0, 1 );


end

