Scripts.OnEnter:
GameTooltip:SetOwner(self, "ANCHOR_TOP");
GameTooltip:SetText("Page One.", 1.0, 0.8, 0.0, 1 );
GameTooltip:AddLine("Death Knight Options", 1.0, 0.8, 0.0, 1 );
GameTooltip:Show();

GameTooltip:SetOwner(self, "ANCHOR_TOP");
GameTooltip:SetText("Page Two.", 1.0, 0.8, 0.0, 1 );
GameTooltip:AddLine("Death Knight Options", 1.0, 0.8, 0.0, 1 );

GameTooltip:Show();

GameTooltip:SetOwner(self, "ANCHOR_TOP");

GameTooltip:SetText("AntiMagic Zone", 1.0, 0.8, 0.0, 1 );
GameTooltip:AddLine("|cFFEDA55FNot Effected by INSTANCE MODE", 1.0, 0.8, 0.0, 1 );
GameTooltip:Show();

GameTooltip:SetOwner(self, "ANCHOR_TOP");

GameTooltip:SetText("Get Under Here", 1.0, 0.8, 0.0, 1 );
GameTooltip:AddLine("This feature work's in Dungeon and Raids Only", 1.0, 0.8, 0.0, 1 );
GameTooltip:AddLine("|cFFEDA55FNot Effected by INSTANCE MODE", 1.0, 0.8, 0.0, 1 );
GameTooltip:Show();

GameTooltip:SetOwner(self, "ANCHOR_TOP");
GameTooltip:SetText("Army of the Dead", 1.0, 0.8, 0.0, 1 );
GameTooltip:Show();

Scripts.OnClick:
if GetMouseButtonClicked() == "LeftButton" then
	GoH_DeathKnight_MINIMAP_TOGGLE();
elseif GetMouseButtonClicked() == "RightButton" then
	GoH_Settings_MINIMAP_TOGGLE();
end
